}

  render(){
 const {esCarroVisible}= this.state
  return(
    <div>
      <Navbar carro={this.state.carro} esCarroVisible={esCarroVisible} mostrarCarro={this.mostrarCarro} />
      <Layout>
        <Title/>
      <Productos
      agregarAlCarro={this.agregarAlCarro}
      productos={this.state.productos} />
      </Layout>
    </div>
  )
}

}



export default App;
