import { Component } from "react";


const styles={
    bubbleAlert:{
        backgroundColor:'#E9725A',
        borderRadius:'15px',
        color:'#fff',
        padding:'2px 10px',
        fontSize: '0.9rem',
        with:'20px'

    }
}
class BurbbleAlert extends Component {
    getNumber(n){
        if(!n){
            return ''
        }
        return n>9 ? '9+' : n
    }
    render() {
        const {value} =this.props
        return (
            <span style={styles.bubbleAlert}>5</span>
        );
    }
}

export default BurbbleAlert;
